def init_files():
    # Check if files exist, create if missing
    for name, (filename, data, palette, width, height) in ASSET_FILES.items():
        try:
            with open(filename, 'rb') as f:
                pass  # File exists
            print(f"{filename} exists, skipping")
        except:
            print(f"Creating {filename}")
            with open(filename, 'wb') as f:
                # Write metadata (width, height, palette)
                f.write(width.to_bytes(2, 'little'))
                f.write(height.to_bytes(2, 'little'))
                f.write(len(palette).to_bytes(1, 'little'))
                for color in palette:
                    f.write(color.to_bytes(2, 'little'))
                # Write RLE data
                f.write(data)

def get_asset(filename):
    with open(filename, 'rb') as f:
        width = int.from_bytes(f.read(2), 'little')
        height = int.from_bytes(f.read(2), 'little')
        palette_len = int.from_bytes(f.read(1), 'little')
        palette = [int.from_bytes(f.read(2), 'little') for _ in range(palette_len)]
        data = bytearray(f.read())
    return width, height, palette, data

# Run initialization
init_files()
